function New-SecureToken { param([int]$Length = 64); $bytes = New-Object byte[] ($Length); $rng = [System.Security.Cryptography.RNGCryptoServiceProvider]::new(); $rng.GetBytes($bytes); $token = [Convert]::ToBase64String($bytes) -replace "[^a-zA-Z0-9]", ""; return $token.Substring(0, [Math]::Min($Length, $token.Length)) }; Write-Host ""; Write-Host "OWNER_TOKEN:" -ForegroundColor Green; $owner = New-SecureToken; Write-Host $owner; Write-Host ""; Write-Host "STAFF_TOKEN:" -ForegroundColor Green; $staff = New-SecureToken; Write-Host $staff; Write-Host ""; Write-Host "CLIENT_REF_PEPPER:" -ForegroundColor Green; $pepper = New-SecureToken; Write-Host $pepper; Write-Host ""; Write-Host "Add these to Cloudflare Worker Secrets!" -ForegroundColor Yellow
